import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { ServiceService } from 'src/app/service/service.service';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css'],
})
export class LoginPageComponent {
  loginForm!: FormGroup;
  registerForm!: FormGroup;
  imagePreview: string | ArrayBuffer | null = null;
  uploadedImage: File | null = null;

  constructor(
    private router: Router,
    private fb: FormBuilder,
    private service: ServiceService,
    private authService: AuthService
  ) {
    // Initialize the login form with validation
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
    });

    // Initialize the register form with validation
    this.registerForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      country_code: ['', Validators.required],
      mobile: ['', Validators.required],
      backup_contact: ['', Validators.required],
      security_contact: ['', Validators.required],
      role: ['', Validators.required],
      image: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  ngOnInit() {}

  // Login functionality
  login(): void {
    if (this.loginForm.invalid) {
      return; // Prevent login if form is invalid
    }

    const formData = this.loginForm.value;
    this.authService.login(formData).subscribe(
      (response: any) => {
        console.log('response',response);
        
        // Assuming the response contains the JWT token and user role
        const token = response.token;
        const role = response.userDetails.role; // You might need to change this based on your API response format

        // Save the token in localStorage
        this.authService.setToken(token);
        this.authService.setRole(role);

        // Redirect based on role
        if (role === 'Admin') {
          console.log('admin role');
          
          this.router.navigate(['/sidebar/dashboard']);
        } else if (role === 'Client') {
          console.log('client role');
          this.router.navigate(['/sidebar/dashboard']); // Client redirection to dashboard
        }
      },
      (error) => {
        alert('Login failed. Please try again.');
      }
    );
  }

  // Handle image upload for user registration
  onImageUpload(event: Event) {
    const fileInput = event.target as HTMLInputElement;
    if (fileInput.files && fileInput.files.length > 0) {
      this.uploadedImage = fileInput.files[0];

      // Show preview of the image
      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result;
      };
      reader.readAsDataURL(this.uploadedImage);
    }
  }

  // Register a new user (with image upload)
  registerUser() {
    if (!this.uploadedImage) {
      console.error('Image file is missing');
      return;
    }

    const formData = new FormData();
    // Append form fields
    Object.keys(this.registerForm.value).forEach((key) => {
      formData.append(key, this.registerForm.value[key]);
    });

    // Append image
    formData.append('image', this.uploadedImage);

    this.service.registerNewuser(formData).subscribe(
      (response: any) => {
        console.log('Response:', response);
        this.registerForm.reset();
        this.imagePreview = null;
      },
      (error: any) => {
        console.error('Error:', error);
      }
    );
  }
}
