import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ServiceService } from 'src/app/service/service.service';

@Component({
  selector: 'app-add-camera',
  templateUrl: './add-camera.component.html',
  styleUrls: ['./add-camera.component.css'],
})
export class AddCameraComponent {
  faceCameraForm: FormGroup;
  serveillanceCameraForm!: FormGroup;
  activeTab: string = 'faceCamera';
  selectedConnectionType: string = '';
  constructor(private fb: FormBuilder, private service: ServiceService) {
    this.faceCameraForm = this.fb.group({
      cameratype: ['face_cam', Validators.required],
      url: [''],
      usb: [''],
    });

    this.serveillanceCameraForm = this.fb.group({
      cameratype: ['incident_cam', Validators.required],
      url: [''],
      usb: [null],
    });
  }
  ngOnInit() {}
  setActiveTab(tab: string) {
    this.activeTab = tab;
  }
  onConnectionTypeChange(event: Event) {
    const target = event.target as HTMLSelectElement;
    this.selectedConnectionType = target.value;

    // Reset unused field
    if (this.selectedConnectionType === 'usb') {
      this.faceCameraForm.get('url')?.reset(); // Clear 'url' field
    } else if (this.selectedConnectionType === 'url') {
      this.faceCameraForm.get('usb')?.reset(); // Clear 'usb' field
    }
  }

  addCamera() {
    if (this.faceCameraForm.valid) {
      let cameraformData = this.faceCameraForm.value;

      // Dynamically remove the field that is not selected based on connection type
      if (this.selectedConnectionType === 'usb') {
        delete cameraformData.url; // Remove URL when USB is selected
      } else if (this.selectedConnectionType === 'url') {
        delete cameraformData.usb; // Remove USB when URL is selected
      }

      console.log('Request data:', cameraformData);

      this.service.addCameraType(cameraformData).subscribe(
        (data: any) => {
          console.log('Response camera', data);
          alert('Form Saved Successfully!');
        },
        (error) => {
          console.error('Error response', error);
          alert('Failed to save the camera!');
        }
      );
    } else {
      alert('Please fill out the form correctly.');
    }
  }



  addserveillanceCamera() {
    if (this.serveillanceCameraForm.valid) {
      // Ensure 'usb' remains null if not set by the user
      const servicameraformData = {
        ...this.serveillanceCameraForm.value,
        usb: null,
      };

      console.log('Form Data:', servicameraformData);

      this.service.addCameraType(servicameraformData).subscribe((data: any) => {
        console.log('Response camera', data);
      });

      alert('Form Saved Successfully!');
    } else {
      alert('Please fill out the form correctly.');
    }
  }
}
<div class="cameraMainDiv">
  <!-- Nav tabs -->
  <ul class="nav nav-tabs">
    <li class="nav-item">
      <a
        class="nav-link"
        [class.active]="activeTab === 'faceCamera'"
        [ngClass]="{ 'custom-active-tab': activeTab === 'faceCamera' }"
        (click)="setActiveTab('faceCamera')"
        data-bs-toggle="tab"
        href="#faceCamera"
      >
        Face Camera
      </a>
    </li>
    <li class="nav-item">
      <a
        class="nav-link"
        [class.active]="activeTab === 'surveillanceCamera'"
        [ngClass]="{ 'custom-active-tab': activeTab === 'surveillanceCamera' }"
        (click)="setActiveTab('surveillanceCamera')"
        data-bs-toggle="tab"
        href="#surveillanceCamera"
      >
        Surveillance Camera
      </a>
    </li>
  </ul>

  <!-- Face Camera Tab content -->
  <div class="tab-content">
    <div id="faceCamera" class="tab-pane fade show active">
      <h4>Face Camera</h4>
      <form
        class="formStyle"
        [formGroup]="faceCameraForm"
        (ngSubmit)="addCamera()"
      >
        <div class="inputClass">
          <label class="form-label">Camera Type</label>
          <input
            id="urlInput"
            type="text"
            class="form-control"
            placeholder="Enter URL"
            formControlName="cameratype"
            readonly
          />
        </div>

        <div class="mb-3 mt-4 inputClass">
          <label for="connectionType" class="form-label"
            >Connection Type <span style="color: red">*</span></label
          >
          <select
            id="connectionType"
            class="form-select"
            (change)="onConnectionTypeChange($event)"
          >
            <option value="" selected>Select Connection</option>
            <option value="usb">USB</option>
            <option value="url">URL</option>
          </select>
        </div>

        <!-- Conditional Input Fields -->
        <div *ngIf="selectedConnectionType === 'usb'" class="mb-3 inputClass">
          <label for="usbInput" class="form-label"
            >USB Input <span style="color: red">*</span></label
          >
          <input
            id="usbInput"
            type="number"
            class="form-control"
            placeholder="Enter USB Input"
            formControlName="usb"
          />
        </div>

        <div *ngIf="selectedConnectionType === 'url'" class="mb-3 inputClass">
          <label for="urlInput" class="inputClass"
            >URL Input <span style="color: red">*</span></label
          >
          <input
            id="urlInput"
            type="text"
            class="form-control"
            placeholder="Enter URL"
            formControlName="url"
          />
        </div>

        <!-- Save Button -->
        <button type="submit" class="btn btn-success">Save</button>
      </form>
    </div>




    <!-- surveillance Camera Tab content -->
    <div id="surveillanceCamera" class="tab-pane fade">
      <h4>Surveillance Camera</h4>
      <form
        class="formStyle"
        [formGroup]="serveillanceCameraForm"
        (ngSubmit)="addserveillanceCamera()"
      >
        <div class="inputClass">
          <label class="form-label">Camera Type</label>
          <input
            id="urlInput"
            type="text"
            class="form-control"
            placeholder="Enter URL"
            formControlName="cameratype"
            readonly
          />
        </div>

        <!-- Conditional Input Fields -->

        <div class="mb-3 mt-5 inputClass">
          <label for="urlInput" class="inputClass"
            >URL Input <span style="color: red">*</span></label
          >
          <input
            id="urlInput"
            type="text"
            class="form-control"
            placeholder="Enter URL"
            formControlName="url"
          />
        </div>

        <!-- Save Button -->
        <button type="submit" class="btn btn-success">Save</button>
      </form>
    </div>
  </div>
</div>
