import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css'],
})
export class LoginPageComponent {
  loginForm!: FormGroup;
  registerForm!: FormGroup;
  imagePreview: string | ArrayBuffer | null = null;


  constructor(private router: Router,
    private fb: FormBuilder
  ) {
    this.loginForm = new FormGroup({
      username: new FormControl('', Validators.required),
      password: new FormControl('', Validators.required),
    });

    this.registerForm = new FormGroup({
      name: new FormControl('', Validators.required),
      email: new FormControl('', Validators.required),
      country_code: new FormControl('', Validators.required),
      mobile: new FormControl('', Validators.required),
      backup_contact: new FormControl('', Validators.required),
      security_contact: new FormControl('', Validators.required),
      person_stay_time_limit: new FormControl('', Validators.required),
      image: new FormControl('', Validators.required),
    });
  }
  ngOnInit() {}

  onclickLogin() {
    console.log(this.loginForm.value);
    const formData = this.loginForm.value;
    if (
      formData.username == 'anemoi@gmail.com' ||
      formData.password == 'Anemoi@123'
    ) {
      this.router.navigate(['/sidebar/dashboard']);
    }
  }
  onImageUpload(event: Event): void {
    const file = (event.target as HTMLInputElement).files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result; // Store the image data for preview
      };
      reader.readAsDataURL(file); // Convert the file to a base64 string
      this.loginForm.patchValue({ image: file }); // Update the form control
      this.loginForm.get('image')?.updateValueAndValidity();
    }
  }
}
