import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { ServiceService } from 'src/app/service/service.service';
import { environment } from 'src/environment/environment';

export interface inciedentdata {
  incident_type: string;
  time: string;
  date: string;
  _id: string;
}

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css'],
})
export class SidebarComponent {
  incidentDataArray: inciedentdata[] = [];
  searchText: string = '';
  p: number = 1;
  notificationCount = 5;
  incidentImageUrl: any;
  incidentVideoUrl: any;
  notifications = [
    'You have a new message.',
    'Your profile was updated.',
    'New comment on your post.',
    'Someone liked your photo.',
    'New event invitation.',
  ]; // Example notification list
  isNotificationOpen = false;
  constructor(
    private router: Router,
    private service: ServiceService,
    private authService: AuthService
  ) {}
  ngOnInit() {}
  onclickLogout() {
    this.authService.logout();

    // Redirect the user to the login page
    this.router.navigate(['/login']);
  }

  displayUser() {
    this.router.navigate(['/sidebar/user']);
  }
  dispalyCameraAdd() {
    this.router.navigate(['/sidebar/camera']);
  }
  displayDashbord() {
    this.router.navigate(['/sidebar/dashboard']);
  }
  toggleNotification() {
    this.isNotificationOpen = !this.isNotificationOpen;
  }


}
