import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css'],
})
export class LoginPageComponent {
  loginForm!: FormGroup;
  constructor() {
    this.loginForm = new FormGroup({
      name: new FormControl('', Validators.required),
      password: new FormControl('', Validators.required),
    });
  }
  ngOnInit() {}
}
<div class="loginDiv">
  <div class="formDiv">
    <form action="" [formGroup]="loginForm">
        <div class="form-floating mb-3">
            <input type="text" formControlName="name" class="form-control" id="floatingInput" placeholder="User Name">
            <label for="floatingInput">User Name</label>
          </div>
          <div class="form-floating">
            <input type="password" formControlName="password" class="form-control" id="floatingPassword" placeholder="Password">
            <label for="floatingPassword">Password</label>
          </div>
    </form>
  </div>
</div>
