import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { ServiceService } from 'src/app/service/service.service';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css'],
})
export class LoginPageComponent {
  loginForm!: FormGroup;
  registerForm!: FormGroup;
  imagePreview: string | ArrayBuffer | null = null;
  uploadedImage: File | null = null;
  toastVisible = false; // Control the visibility of the toast
  toastMessage = ''; // Server message to be shown in the toast

  constructor(
    private router: Router,
    private fb: FormBuilder,
    private service: ServiceService,
    private authService: AuthService
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
    });

    this.registerForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      country_code: ['', Validators.required],
      mobile: ['', Validators.required],
      backup_contact: ['', Validators.required],
      security_contact: ['', Validators.required],
      role: ['', Validators.required],
      image: ['', Validators.required],
      password: ['', Validators.required],
    });
  }

  ngOnInit() {}

  // Login functionality
  login(): void {
    if (this.loginForm.invalid) {
      return;
    }

    const formData = this.loginForm.value;
    this.authService.login(formData).subscribe(
      (response: any) => {
        const token = response.token;
        const role = response.userDetails.role;

        // Save token and role
        this.authService.setToken(token);
        this.authService.setRole(role);

        // Display success message
        this.toastMessage = 'Login successful!';
        this.showToast();

        // Redirect based on the role
        if (role === 'Admin') {
          this.router.navigate(['/sidebar/dashboard']);
        } else if (role === 'Client') {
          this.router.navigate(['/sidebar/dashboard']);
        }
      },
      (error) => {
        this.toastMessage = 'Login failed. Please try again.';
        this.showToast();
      }
    );
  }

  // Handle image upload for user registration
  onImageUpload(event: Event) {
    const fileInput = event.target as HTMLInputElement;
    if (fileInput.files && fileInput.files.length > 0) {
      this.uploadedImage = fileInput.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result;
      };
      reader.readAsDataURL(this.uploadedImage);
    }
  }

  // Register a new user (with image upload)
  registerUser() {
    if (!this.uploadedImage) {
      console.error('Image file is missing');
      return;
    }

    const formData = new FormData();
    Object.keys(this.registerForm.value).forEach((key) => {
      formData.append(key, this.registerForm.value[key]);
    });
    formData.append('image', this.uploadedImage);

    this.service.registerNewuser(formData).subscribe(
      (response: any) => {
        this.toastMessage = 'Registration successful!';
        this.showToast();
        this.registerForm.reset();
        this.imagePreview = null;
      },
      (error: any) => {
        this.toastMessage = 'Registration failed. Please try again.';
        this.showToast();
      }
    );
  }

  // Show toast
  showToast() {
    this.toastVisible = true;
    setTimeout(() => {
      this.toastVisible = false;
    }, 3000); // Hide after 3 seconds
  }
  hideToast(): void {
    this.toastVisible = false;
  }
}
<div class="loginDiv">
  <div class="formDiv">
    <div class="login">Login</div>
    <form action="" [formGroup]="loginForm">
      <div class="form-floating inputClass mb-3">
        <input
          type="text"
          formControlName="email"
          class="form-control"
          placeholder="User Name"
        />
        <label for="floatingInput"
          >User Name <span style="color: red">*</span></label
        >
      </div>
      <div class="form-floating inputClass">
        <input
          type="password"
          formControlName="password"
          class="form-control"
          placeholder="Password"
        />
        <label for="floatingPassword"
          >Password <span style="color: red">*</span></label
        >
      </div>
      <span
        class="SignUp"
        data-bs-toggle="modal"
        data-bs-target="#staticBackdrop"
        >Add New User</span
      >
    </form>
    <button
      type="button"
      [disabled]="!loginForm.valid"
      (click)="login()"
      class="btn btn-light loginButton"
      id="liveToastBtn"
    >
      Login
    </button>
  </div>
</div>

<!-- toast for login message  -->
<div class="toast-container position-fixed bottom-0 end-0 p-3">
  <div
    id="liveToast"
    class="toast"
    role="alert"
    aria-live="assertive"
    aria-atomic="true"
    [class.show]="toastVisible"
  >
    <div class="toast-header">
      <strong class="me-auto">Server Response</strong>
      <small>Just now</small>
      <button
        type="button"
        class="btn-close"
        data-bs-dismiss="toast"
        aria-label="Close"
        (click)="hideToast()"
      ></button>
    </div>
    <div class="toast-body">
      {{ toastMessage }}
    </div>
  </div>
</div>
<!-- Register new user  -->
<div class="modal fade" id="staticBackdrop">
  <div class="modal-dialog modal-dialog-scrollable modal-lg">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="staticBackdropLabel">Register User</h1>
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body">
        <form action="" [formGroup]="registerForm">
          <div class="row g-2">
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="text"
                  formControlName="name"
                  class="form-control"
                  placeholder="User Name"
                />
                <label for="floatingInput"
                  >User Name <span style="color: red">*</span></label
                >
              </div>
            </div>
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="text"
                  formControlName="email"
                  class="form-control"
                  placeholder="Enter email"
                />
                <label for="floatingInputGrid"
                  >Email address <span style="color: red">*</span></label
                >
              </div>
            </div>
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="password"
                  formControlName="password"
                  class="form-control"
                  placeholder="Password"
                />
                <label for="floatingInputGrid"
                  >Password <span style="color: red">*</span></label
                >
              </div>
            </div>
          </div>
          <div class="row g-2">
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="text"
                  formControlName="country_code"
                  class="form-control"
                  placeholder="Country code"
                />
                <label for="floatingInputGrid"
                  >Country code <span style="color: red">*</span></label
                >
              </div>
            </div>
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="text"
                  formControlName="mobile"
                  class="form-control"
                  placeholder="Mobile No"
                />
                <label for="floatingInputGrid"
                  >Mobile Number <span style="color: red">*</span></label
                >
              </div>
            </div>
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="text"
                  formControlName="backup_contact"
                  class="form-control"
                  placeholder="Backup person contact"
                />
                <label for="floatingInputGrid"
                  >Backup contact <span style="color: red">*</span></label
                >
              </div>
            </div>
          </div>
          <div class="row g-2">
            <div class="col-md">
              <div class="form-floating inputClass">
                <input
                  type="text"
                  formControlName="security_contact"
                  class="form-control"
                  placeholder="Security contact"
                />
                <label for="floatingInputGrid"
                  >Security contact <span style="color: red">*</span></label
                >
              </div>
            </div>
            <div class="col-md">
              <div class="dropdown inputClass">
                <select
                  class="form-select form-control"
                  formControlName="role"
                  aria-label="Small select example"
                >
                  <option value="" selected disabled class="placeholder">
                    Select Role
                  </option>
                  <option value="Admin">Admin</option>
                  <option value="Client">Client</option>
                </select>
              </div>
            </div>
            <div class="col-md">
              <div class="ImageinputClass">
                <input
                  type="file"
                  formControlName="image"
                  class="form-control"
                  id="imageUpload"
                  (change)="onImageUpload($event)"
                  accept="image/*"
                />
              </div>
              <div *ngIf="imagePreview" class="image-preview">
                <img [src]="imagePreview" alt="Uploaded Image" />
              </div>
            </div>
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button
          type="button"
          class="btn btn-outline Cancelregister"
          data-bs-dismiss="modal"
        >
          Cancel
        </button>
        <button
          type="button"
          data-bs-dismiss="modal"
          (click)="registerUser()"
          class="btn btn-primary saveRegister"
        >
          Register
        </button>
      </div>
    </div>
  </div>
</div>
