import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ServiceService } from 'src/app/service/service.service';

export interface userdata {
  name: string;
  mobile: string;
  email: string;
  _id: string;
  role: string;
  backup_contact: string;
  security_contact: string;
  user_id: any;
}
@Component({
  selector: 'app-user-data',
  templateUrl: './user-data.component.html',
  styleUrls: ['./user-data.component.css'],
})
export class UserDataComponent implements OnInit {
  userDataArray: userdata[] = [];
  registerForm: FormGroup;
  searchText: string = '';
  p: number = 1;
  selectedUserId: string | null = null;
  imagePreview: string | null = null;

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private service: ServiceService
  ) {
    this.registerForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
      country_code: ['', Validators.required],
      mobile: ['', Validators.required],
      backup_contact: ['', Validators.required],
      security_contact: ['', Validators.required],
      role: ['', Validators.required],
      image: [null],
    });
  }

  ngOnInit() {
    this.service.getuserData().subscribe((userData: any) => {
      this.userDataArray = userData;
    });
  }

  filteredData() {
    if (!this.searchText) {
      return this.userDataArray;
    }
    return this.userDataArray.filter((item) =>
      Object.values(item).some((val) =>
        val.toString().toLowerCase().includes(this.searchText.toLowerCase())
      )
    );
  }

  editUser(userId: string) {
    this.selectedUserId = userId;
    const user = this.userDataArray.find((u) => u._id === userId);
    if (user) {
      this.registerForm.patchValue({
        name: user.name,
        email: user.email,
        country_code: '', // Add if applicable
        mobile: user.mobile,
        backup_contact: user.backup_contact,
        security_contact: user.security_contact,
        role: user.role,
        image: null, // No need for an image here
      });
      this.imagePreview = null; // Reset image preview
    }
  }

  onImageUpload(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result as string;
        this.registerForm.patchValue({ image: file });
      };
      reader.readAsDataURL(file);
    }
  }

  registerUser() {
    if (this.selectedUserId) {
      const updatedData = this.registerForm.value;
      this.service.updateUser(this.selectedUserId, updatedData).subscribe(
        (response) => {
          alert('User updated successfully.');
          this.ngOnInit(); // Refresh data
        },
        (error) => {
          alert('Failed to update user.');
          console.error(error);
        }
      );
    }
  }
  deleteUser(userId: any) {
    const isConfirmed = confirm('Are you sure you want to delete this user?');
    if (isConfirmed) {
      this.service.deleteUser(userId).subscribe(
        () => {
          alert('User deleted successfully.');
          console.log('User deleted successfully.');
          this.ngOnInit(); // Refresh data after deletion
        },
        (error) => {
          alert('Failed to delete the user. Please try again.');
          console.error('Error deleting user:', error);
        }
      );
    } else {
      console.log('User deletion cancelled.');
    }
  }
}
<div class="tableHeader">
  Users
  <!-- <button type="button" class="btn btn-outline-success DownloadButton">
      Download Report
    </button> -->
</div>
<hr class="hrStyle" />
<div class="content">
  <input
    type="text"
    class="form-control searchBox"
    [(ngModel)]="searchText"
    placeholder="Search incidents..."
  />
  <table class="table table-striped">
    <thead>
      <tr>
        <th scope="col">Sr no</th>
        <th scope="col">User Name</th>
        <th scope="col">Role</th>
        <th scope="col">Mobile Number</th>
        <th scope="col">Email</th>
        <th scope="col">Backup contact</th>
        <th scope="col">Security contact</th>
        <th scope="col">Action</th>
      </tr>
    </thead>
    <tbody>
      <tr
        *ngFor="
          let userData of filteredData()
            | paginate : { itemsPerPage: 5, currentPage: p };
          let i = index
        "
      >
        <th scope="row">{{ i + 1 + (p - 1) * 5 }}</th>
        <td>{{ userData.name }}</td>
        <td>{{ userData.role }}</td>
        <td>{{ userData.mobile }}</td>
        <td>{{ userData.email }}</td>
        <td>{{ userData.backup_contact }}</td>
        <td>{{ userData.security_contact }}</td>
        <td>
          <button
          data-bs-toggle="modal"
          data-bs-target="#staticBackdrop"
          type="button"
          class="btn btn-outline-success ImageButton"
          (click)="editUser(userData._id)"
        >
          <i class="bi bi-pencil-square"></i>
        </button>

          <button
            data-bs-toggle="modal"
            data-bs-target="#staticBackdropVideo"
            type="button"
            class="btn btn-outline-danger VideoButton"
            (click)="deleteUser(userData.user_id)"
          >
            <i class="bi bi-trash"></i>
            <!-- Delete Icon -->
          </button>
        </td>
      </tr>
    </tbody>
  </table>

<!-- edit user -->
