import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService } from 'src/app/service/service.service';
import { environment } from 'src/environment/environment';
export interface inciedentdata {
  incident_type: string;
  time: string;
  date: string;
  _id: string;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  @ViewChild('videoPlayer', { static: false }) videoPlayer!:
    | ElementRef
    | undefined;
  incidentDataArray: inciedentdata[] = [];
  searchText: string = '';
  p: number = 1;
  incidentImageUrl: any;
  incidentVideoUrl: any;
  itemsPerPage: any = 5;
  constructor(
    private router: Router,
    private service: ServiceService,
    private renderer: Renderer2
  ) {}
  ngOnInit() {
    this.loadIncidentData();
    this.service.notificationReceived$.subscribe(() => {
      console.log('New notification received. Refreshing dashboard data...');
      this.loadIncidentData();
    });
  }
  getIndex(i: number): number {
    return i + 1 + (Math.max(this.p, 1) - 1) * this.itemsPerPage;
  }

  loadIncidentData() {
    this.service.getIncedeentData(this.p, this.itemsPerPage).subscribe(
      (incidentData: any) => {
        this.incidentDataArray = incidentData.incidents || [];
        // Sort by date and time in descending order
        this.incidentDataArray.sort((a, b) => {
          const dateA = new Date(`${a.date}T${a.time}`).getTime();
          const dateB = new Date(`${b.date}T${b.time}`).getTime();
          return dateB - dateA;
        });
        this.p = 1; // Reset pagination to first page when data is refreshed
        console.log('Incident Data:', this.incidentDataArray);
      },
      (error) => {
        console.error('Failed to load incident data:', error);
      }
    );
  }
  onclickLogout() {
    this.router.navigate(['/login']);
  }

  filteredData() {
    let data = this.incidentDataArray;

    if (this.searchText) {
      data = data.filter((item) =>
        Object.values(item).some((val) =>
          val.toString().toLowerCase().includes(this.searchText.toLowerCase())
        )
      );
    }

    return data.sort((a, b) => {
      const dateA = new Date(`${a.date}T${a.time}`).getTime();
      const dateB = new Date(`${b.date}T${b.time}`).getTime();
      return dateB - dateA; // Descending order
    });
  }

  onshowImage(imageId: any) {
    if (!imageId) {
      console.error('Invalid image ID');
      return;
    }

    this.incidentImageUrl = `${environment.url}/getIncidentImage/${imageId}`;
    console.log(this.incidentImageUrl);

    // Optional: Verify image load
    const img = new Image();
    img.src = this.incidentImageUrl;
    img.onload = () => console.log('Image loaded successfully');
    img.onerror = () => console.error('Failed to load image');
  }

  onshowVideo(videoId: any) {
    if (!videoId) {
      console.error('Invalid video ID');
      return;
    }

    this.incidentVideoUrl = `${environment.url}/getIncidentVideo/${videoId}`;
    // console.log('Video URL:', this.incidentVideoUrl);

    // Optional: Verify video load
    this.reloadVideo();
  }
  reloadVideo(): void {
    if (this.videoPlayer && this.videoPlayer.nativeElement) {
      this.renderer.setAttribute(
        this.videoPlayer.nativeElement,
        'src',
        this.incidentVideoUrl
      );
      this.videoPlayer.nativeElement.load();
    }
  }
}
<div class="tableHeader">
  Incident Data
  <!-- <button type="button" class="btn btn-outline-success DownloadButton">
      Download Report
    </button> -->
</div>
<hr class="hrStyle" />
<div class="content">
  <input
    type="text"
    class="form-control searchBox"
    [(ngModel)]="searchText"
    placeholder="Search incidents..."
  />
  <table class="table table-striped">
    <thead>
      <tr>
        <th scope="col">Sr no</th>
        <th scope="col">Incident Name</th>
        <th scope="col">Date</th>
        <th scope="col">Time</th>
        <th scope="col">Action</th>
      </tr>
    </thead>
    <tbody>
      <tr
        *ngFor="
          let rowData of filteredData()
            | paginate : { itemsPerPage: 5, currentPage: p };
          let i = index
        "
      >
        <th scope="row">{{ getIndex(i) }}</th>
        <td>{{ rowData.incident_type }}</td>
        <td>{{ rowData.date }}</td>
        <td>{{ rowData.time }}</td>
        <td>
          <button
            data-bs-toggle="modal"
            data-bs-target="#staticBackdropImage"
            type="button"
            class="btn btn-outline-success ImageButton"
            (click)="onshowImage(rowData._id)"
          >
            <i class="bi bi-image"></i>
          </button>
          <button
            data-bs-toggle="modal"
            data-bs-target="#staticBackdropVideo"
            type="button"
            class="btn btn-outline-success VideoButton"
            (click)="onshowVideo(rowData._id)"
          >
            <i class="bi bi-camera-video"></i>
          </button>
        </td>
      </tr>
    </tbody>
  </table>

  <!-- Pagination Controls -->
  <div class="pagination-container">
    <pagination-controls (pageChange)="p = $event"></pagination-controls>
  </div>
  <div *ngIf="incidentDataArray.length === 0">
    <p style="color: white">No incidents found...</p>
  </div>
</div>

<!-- image dailog  -->

<div
  class="modal fade"
  id="staticBackdropImage"
  data-bs-backdrop="static"
  data-bs-keyboard="false"
  tabindex="-1"
  aria-labelledby="staticBackdropLabel"
  aria-hidden="true"
>
  <div class="modal-dialog modal-dialog-centered">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="staticBackdropLabel">
          Incident Image
        </h1>
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body">
        <img
          style="width: 100%; height: auto"
          [src]="incidentImageUrl"
          alt="Incident Image"
          (error)="incidentImageUrl = 'assets/images/default-placeholder.png'"
        />
      </div>
      <div class="modal-footer">
        <button
          type="button"
          class="btn btn-outline-success cancelImage"
          data-bs-dismiss="modal"
        >
          Close
        </button>
      </div>
    </div>
  </div>
</div>

