import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { ServiceService } from 'src/app/service/service.service';
import { environment } from 'src/environment/environment';

export interface inciedentdata {
  incident_type: string;
  time: string;
  date: string;
  _id: string;
}

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css'],
})
export class SidebarComponent {
  incidentDataArray: inciedentdata[] = [];
  searchText: string = '';
  p: number = 1;
  isNotificationOpen = false;
  notifications: string[] = ['Camera disconnected', 'Motion detected', 'Low battery'];
  notificationCount = this.notifications.length;
  showNotificationPreview = false;
  selectedNotification: string | null = null;
  selectedNotificationIndex: number | null = null;
  constructor(
    private router: Router,
    private service: ServiceService,
    private authService: AuthService
  ) {}
  ngOnInit() {}
  onclickLogout() {
    this.authService.logout();

    // Redirect the user to the login page
    this.router.navigate(['/login']);
  }

  displayUser() {
    this.router.navigate(['/sidebar/user']);
  }
  dispalyCameraAdd() {
    this.router.navigate(['/sidebar/camera']);
  }
  displayDashbord() {
    this.router.navigate(['/sidebar/dashboard']);
  }
  toggleNotification() {
    this.isNotificationOpen = !this.isNotificationOpen;
  }


  showPreview(notification: string, index: number) {
    this.selectedNotification = notification;
    this.selectedNotificationIndex = index;
    this.showNotificationPreview = true;
  }

  closePreview() {
    if (this.selectedNotificationIndex !== null) {
      // Remove the selected notification
      this.notifications.splice(this.selectedNotificationIndex, 1);
      this.notificationCount = this.notifications.length;

      // Reset selected notification and preview
      this.selectedNotification = null;
      this.selectedNotificationIndex = null;
      this.showNotificationPreview = false;
    }
  }

}
<div class="sidebarComponent">
  <div class="CompanyLogo">Home Security</div>
  <div class="navBar">
    <ul class="inline-list">
      <li (click)="displayDashbord()">Dashboard</li>
      <li (click)="dispalyCameraAdd()">Add Camera</li>
      <li (click)="displayUser()">User</li>
    </ul>
  </div>
  <div class="logOut">
    <div class="notification-icon" (click)="toggleNotification()">
      <i class="bi bi-bell" style="font-size: 24px" title="Notifications"></i>
      <span class="notification-badge">{{ notificationCount }}</span>
    </div>

    <!-- Notification Dropdown -->
    <div *ngIf="isNotificationOpen" class="notification-dropdown">
      <ul>
        <li
          *ngFor="let notification of notifications; let i = index"
          (click)="showPreview(notification, i)"
        >
          {{ notification }}
        </li>
      </ul>
    </div>

    <span class="onclickLogout" (click)="onclickLogout()">LogOut</span>
  </div>
</div>

<!-- Notification Preview Modal -->
<div *ngIf="showNotificationPreview" class="notification-preview">
  <div class="preview-content">
    <p>{{ selectedNotification }}</p>
    <button
      type="button"
      (click)="closePreview()"
      class="btn btn-outline-success cancelImage"
      data-bs-dismiss="modal"
    >
      Close
    </button>
  </div>
</div>

<div class="content">
  <router-outlet></router-outlet>
</div>

<div class="footer">Footer Content</div>
