import { Injectable } from '@angular/core';
import { Socket } from 'ngx-socket-io';

@Injectable({
  providedIn: 'root'
})
export class WebSocketService {
  newNotification$ = this.socket.fromEvent<string>('newNotification'); // Listen for new notifications


  constructor(private socket: Socket) {}

  // Emit custom events if needed
  emitEvent(eventName: string, payload: any): void {
    this.socket.emit(eventName, payload);
    console.log('eventName :',eventName ,'payload :',payload);
    
  }
}
