import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ServiceService } from 'src/app/service/service.service';
import { environment } from 'src/environment/environment';
export interface inciedentdata {
  incident_type: string;
  time: string;
  date: string;
  _id: string;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  incidentDataArray: inciedentdata[] = [];
  searchText: string = '';
  p: number = 1;
  incidentImageUrl: any;
  incidentVideoUrl: any;
  constructor(private router: Router, private service: ServiceService) {}
  ngOnInit() {
    this.service.getIncedeentData().subscribe((incidentData: any) => {
      this.incidentDataArray = incidentData.incidents;
      console.log('incidentData', this.incidentDataArray);

      if (this.incidentDataArray.length === 0) {
        this.p = 0;
      }
    });
  }
  onclickLogout() {
    this.router.navigate(['/login']);
  }
  filteredData() {
    if (!this.searchText) {
      return this.incidentDataArray;
    }
    return this.incidentDataArray.filter((item) =>
      Object.values(item).some((val) =>
        val.toString().toLowerCase().includes(this.searchText.toLowerCase())
      )
    );
  }

  onshowImage(imageId: any) {
    if (!imageId) {
      console.error('Invalid image ID');
      return;
    }

    this.incidentImageUrl = `${environment.url}/getIncidentImage/${imageId}`;
    console.log(this.incidentImageUrl);

    // Optional: Verify image load
    const img = new Image();
    img.src = this.incidentImageUrl;
    img.onload = () => console.log('Image loaded successfully');
    img.onerror = () => console.error('Failed to load image');
  }

  onshowVideo(videoId: any) {
    if (!videoId) {
      console.error('Invalid video ID');
      return;
    }

    this.incidentVideoUrl = `${environment.url}/getIncidentVideo/${videoId}`;
    // console.log('Video URL:', this.incidentVideoUrl);

    // Optional: Verify video load
    const video = document.createElement('video');
    video.src = this.incidentVideoUrl;
    video.onloadeddata = () => console.log('Video loaded successfully');
    video.onerror = () => console.error('Failed to load video');
  }

}
